--TABLA DISTRITOS

CREATE TABLE DISTRITOS
( CODDIS VARCHAR2(4) NOT NULL,
  NOMDIS VARCHAR2(20) NOT NULL, 
CONSTRAINT pk_distritos PRIMARY KEY ( CODDIS )
);


--TABLA LOCALES

CREATE TABLE LOCALES (
CODLOC VARCHAR2(4) NOT NULL,
NOMLOC VARCHAR2(40) NOT NULL,
DIRECC VARCHAR2(30) NOT NULL,
CODDIS VARCHAR2(4) NOT NULL,
TELEF VARCHAR2(9) NULL, 
CONSTRAINT pk_locales PRIMARY KEY ( CODLOC ),
    CONSTRAINT fk_locales_distritos 
        FOREIGN KEY ( CODDIS ) 
        REFERENCES distritos ( CODDIS )
);


-- CREANDO TABLA CLIENTES
CREATE TABLE CLIENTES (
CODCLI VARCHAR2(4) NOT NULL,
NOMCLI VARCHAR2(30) NOT NULL,
DNI VARCHAR2(8) NOT NULL,
SEXO VARCHAR2(1) NOT NULL,
DIREC VARCHAR2(30) NOT NULL,
CODDIS VARCHAR2(4) NOT NULL,
TELEF VARCHAR2(9) NULL,
EMAIL VARCHAR2(30) NULL, 
CONSTRAINT pk_clientes PRIMARY KEY ( CODCLI ),
    CONSTRAINT fk_clientes_distritos 
        FOREIGN KEY ( CODDIS ) 
        REFERENCES distritos ( CODDIS )
);

--CREANDO TABLA PERSONAL
CREATE TABLE PERSONAL (
CODPER VARCHAR2(4) NOT NULL,
NOMPER VARCHAR2(30) NOT NULL,
DNI VARCHAR2(8) NOT NULL,
SEXO VARCHAR2(1) NOT NULL,
DIREC VARCHAR2(40) NOT NULL,
CODDIS VARCHAR2(4) NOT NULL,
TELEF VARCHAR2(9)  NULL,
CODLOC VARCHAR2(4) NOT NULL,
SUELDO DECIMAL(10,2) NOT NULL, 
CONSTRAINT pk_personal PRIMARY KEY ( CODPER ),
    CONSTRAINT fk_personal_distritos 
        FOREIGN KEY ( CODDIS ) 
        REFERENCES distritos ( CODDIS ),
    CONSTRAINT fk_personal_locales 
        FOREIGN KEY ( CODLOC ) 
        REFERENCES locales ( CODLOC )
);

--CREANDO TABLA PROVEEDORES
CREATE TABLE PROVEEDORES (
CODPROV VARCHAR2(4) NOT NULL,
NOMPROV VARCHAR2(30) NOT NULL,
RUC VARCHAR2(10) NOT NULL,
DIREC VARCHAR2(40) NOT NULL,
CODDIS VARCHAR2(4) NOT NULL,
TELEF VARCHAR2(9)  NULL,
EMAIL VARCHAR2(30) NULL,
CONTACTO VARCHAR2(30)  NULL, 
CONSTRAINT pk_proveedores PRIMARY KEY ( CODPROV ),
    CONSTRAINT fk_proveedores_distritos 
        FOREIGN KEY ( CODDIS ) 
        REFERENCES distritos ( CODDIS )
);

--CREANDO TABLA MARCAS
CREATE TABLE MARCAS (
CODMAR VARCHAR2(4) NOT NULL,
NOMMAR VARCHAR2(30) NOT NULL, 
CONSTRAINT pk_marcas PRIMARY KEY ( CODMAR )
);

--CREANDO TABLA CATEGORIAS
CREATE TABLE CATEGORIAS (
CODCAT VARCHAR2(4) NOT NULL,
NOMCAT VARCHAR2(25) NOT NULL, 
CONSTRAINT pk_categorias PRIMARY KEY ( CODCAT )
);

--CREANDO TABLA PRODUCTOS 
CREATE TABLE PRODUCTOS
(
CODPROD VARCHAR2(4) NOT NULL,
NOMPROD VARCHAR2(30) NOT NULL,
CODMAR VARCHAR2(4) NOT NULL,
CODCAT VARCHAR2(4) NOT NULL,
PRECIO DECIMAL(7,2) NOT NULL,
STOCK INT  NULL, 
CONSTRAINT pk_productos PRIMARY KEY ( CODPROD ),
    CONSTRAINT fk_productos_marcas 
        FOREIGN KEY ( CODMAR ) 
        REFERENCES marcas ( CODMAR ),
    CONSTRAINT fk_productos_categorias 
        FOREIGN KEY ( CODCAT ) 
        REFERENCES categorias ( CODCAT )

);

-- CREANDO TABLA FORMASDEPAGO
CREATE TABLE FORMAS_PAGO(
CODFOR VARCHAR2(4) NOT NULL,
NOMFOR VARCHAR2(15) NOT NULL, 
CONSTRAINT pk_formasdepago PRIMARY KEY ( CODFOR )
);

-- CREANDO TABLA COMPROBANTECABECERA
CREATE TABLE COMPROBANTECABECERA (
CODCOMPR VARCHAR2(6) NOT NULL,
CODCLI VARCHAR2(4) NOT NULL,
CODPER VARCHAR2(4) NOT NULL,
CODLOC VARCHAR2(4) NOT NULL,
CODFOR VARCHAR2(4) NOT NULL,
TIPO_COM VARCHAR2(1) NOT NULL,
FECHA  DATE NOT NULL, 
CONSTRAINT pk_comprobantecabecera PRIMARY KEY ( CODCOMPR ),
    CONSTRAINT fk_comprobantecabecera_clientes 
        FOREIGN KEY ( CODCLI ) 
        REFERENCES clientes ( CODCLI ),
    CONSTRAINT fk_comprobantecabecera_personal 
        FOREIGN KEY ( CODPER ) 
        REFERENCES personal ( CODPER ),
    CONSTRAINT fk_comprobantecabecera_locales 
        FOREIGN KEY ( CODLOC ) 
        REFERENCES locales ( CODLOC ),
    CONSTRAINT fk_comprobantecabecera_formasdepago 
        FOREIGN KEY ( CODFOR ) 
        REFERENCES formas_pago ( CODFOR )
);

-- CREANDO TABLA COMPROBANTEDETALLE
CREATE TABLE COMPROBANTEDETALLE (
CODCOMPRD VARCHAR2(6)NOT NULL,
CODPROD VARCHAR2(4) NOT NULL,
CANTIDAD INT NOT NULL,
PRECIO DECIMAL(7,2) NOT NULL,
DCTO DECIMAL(7,2)  NULL, 
CONSTRAINT pk_comprobantedetalle PRIMARY KEY ( CODCOMPRD ),
    CONSTRAINT fk_comprobantedetalle_productos 
        FOREIGN KEY ( CODPROD ) 
        REFERENCES productos ( CODPROD )
);


--CREANDO TABLA ABASTECIMIENTOPRODUCTO
CREATE TABLE ABASTECIMIENTOPRODUCTO(
COD_ABAST_PROD VARCHAR2(6) NOT NULL,
CODPROV VARCHAR2(4) NOT NULL,
CODPROD VARCHAR2(4) NOT NULL, 
CONSTRAINT pk_bastecimientoproducto PRIMARY KEY ( COD_ABAST_PROD ),
    CONSTRAINT fk_abastecimientoproductos_provedores 
        FOREIGN KEY ( CODPROV ) 
        REFERENCES provedores ( CODPROV ),
    CONSTRAINT fk_abastecimientoproductos_productos
        FOREIGN KEY ( CODPROD ) 
        REFERENCES productos ( CODPROD )
);



